            self._respond(404, {"Content-Type": "text/html"}, b"<html><body>404 Not Found</body></html>")

    def do_POST(self):
        content_length = int(self.headers['Content-Length'])
        post_data = self.rfile.read(content_length)
        parsed_path = urlparse(self.path)
        if parsed_path.path == "/upload":
            file_path = parsed_path.path[7:]
            self.static_files[file_path] = post_data
            self._respond(200, {"Content-Type": "text/html"}, b"<html><body>File uploaded</body></html>")
        else:
            self._respond(404, {"Content-Type": "text/html"}, b"<html><body>404 Not Found</body></html>")

    def read_chunks(self):
        chunk_size = 4096
        while True:
            chunk = self.rfile.read(chunk_size)
            if len(chunk) == 0:
                break
            yield chunk

    def end_headers(self):
        self.wfile.write(b"\r\n")

    def send_header(self, header, value):
        self.wfile.write(f"{header}: {value}\r\n".encode())

    def send_response(self, code, message=None):
        self.wfile.write(f"HTTP/1.1 {code} {message}\r\n".encode())

