            self.send_response(status)
            self.send_header("Content-type", type)
            self.end_headers()

        def do_GET(self):
            status = 200
            type = "application/json"
            data = None

            file = self.path.rpartition("/")[2]
            ext = file.rpartition(".")[2]

            if ext == "css":
                data = open(os.path.join(self.DIR_SASS, "PVEDiscordDark.css"), "rb").read()
                type = "text/css"
            elif ext == "js":
                data = open(os.path.join(self.DIR_JS, "PVEDiscordDark.js"), "rb").read()
                type = "application/javascript"
            elif ext in ("png", "jpg", "jpeg"):
                try:
                    data = open(os.path.join(self.DIR_IMAGES, file), "rb").read()
                    type = f"image/{ext}"
                except FileNotFoundError:
                    status = 404
            elif ext == "svg":
                try:
                    data = open(os.path.join(self.DIR_IMAGES, file), "rb").read()
                    type = f"image/svg+xml"
                except FileNotFoundError:
                    status = 404
            else:
                status = 400
            self._set_headers(status, type)
            if status == 200:
                self.wfile.write(data)
            else:
                self.wfile.write(json.dumps({"error": status}).encode())

    def run(self):
