        server = HTTPServer(
            server_address=("", self.PORT),
            RequestHandlerClass=lambda *args, **kwargs: self.RequestHandler(
                *args,
                DIR_SASS=self.DIR_SASS,
                DIR_IMAGES=self.DIR_IMAGES,
                DIR_JS=self.DIR_JS,
                **kwargs
            )
        )
        try:
            server.serve_forever()
        except KeyboardInterrupt:
            quit()

