            raise ValueError('Unsupported URL scheme %r' % self.__url.scheme)

        if self.__url.port is None:
            port = httplib.HTTPS_PORT if self.__url.scheme == 'https' else httplib.HTTP_PORT
        else:
            port = self.__url.port
        self.__id_count = 0

        if authpair is None:
            self.__auth_header = None
        else:
            authpair = authpair.encode('utf8')
            self.__auth_header = b"Basic " + base64.b64encode(authpair)

        if connection:
            self.__conn = connection
        else:
            if self.__url.scheme == 'https':
                self.__conn = httplib.HTTPSConnection(self.__url.hostname, port=port, timeout=timeout)
            else:
                self.__conn = httplib.HTTPConnection(self.__url.hostname, port=port, timeout=timeout)

    def _call(self, service_name, *args):
        self.__id_count += 1

        postdata = json.dumps({
            'version': '1.1',
            'method': service_name,
            'params': args,
            'id': self.__id_count
        })

        headers = {
            'Host': self.__url.hostname,
            'User-Agent': DEFAULT_USER_AGENT,
            'Content-type': 'application/json',
        }

        if self.__auth_header is not None:
            headers['Authorization'] = self.__auth_header

        self.__conn.request('POST', self.__url.path, postdata, headers)
        response = self._get_response()

        err = response.get('error')
        if err is not None:
            if isinstance(err, dict):
                raise JSONRPCError({
                    'code': err.get('code', -345),
                    'message': err.get('message', 'error message not specified')
                })
            raise JSONRPCError({'code': -344, 'message': str(err)})
        elif 'result' not in response:
            raise JSONRPCError({
                'code': -343, 'message': 'missing JSON-RPC result'
            })
        else:
            return response['result']

    def _get_response(self):
        http_response = self.__conn.getresponse()
        if http_response is None:
            raise JSONRPCError({
                'code': -342, 'message': 'missing HTTP response from server'
            })

        rdata = http_response.read().decode('utf8')
        try:
            return json.loads(rdata)
        except Exception:
            raise JSONRPCError({
                'code': -342,
                'message': ('non-JSON HTTP response with \'%i %s\' from server: \'%.20s%s\''
                            % (http_response.status, http_response.reason,
                               rdata, '...' if len(rdata) > 20 else ''))
            })

    def close(self):
        if self.__conn is not None:
            self.__conn.close()

    def __del__(self):
        if self.__conn is not None:
            self.__conn.close()

