class socksocket(socket.socket):
    default_proxy = None

    def __init__(self, family, type, proto, *args, **kwargs):
        super().__init__(family, type, proto, *args, **kwargs)
        self.proxy = None
        self.proxy_sockname = None
        self.proxy_peername = None
        self._timeout = None

    def set_proxy(self, proxy_type, addr, port, rdns=False, username=None, password=None):
        self.proxy = (proxy_type, addr, port, rdns, username, password)

    def connect(self, dest_pair):
        proxy_type, proxy_addr, proxy_port, rdns, username, password = self.proxy
        if proxy_type == 'SOCKS4':
            self._negotiate_SOCKS4(dest_pair)
        elif proxy_type == 'SOCKS5':
            self._negotiate_SOCKS5(dest_pair)
        elif proxy_type == 'HTTP':
            self._negotiate_HTTP(dest_pair)
        else:
            super().connect(dest_pair)

    def _readall(self, file, count):
        data = bytearray()
        while len(data) < count:
            more = file.read(count - len(data))
            if not more:
                raise EOFError
            data.extend(more)
        return data

    def _proxy_addr(self):
        proxy_type, proxy_addr, proxy_port = self.proxy
        return (proxy_addr, proxy_port)

    def _negotiate_SOCKS4(self, dest_addr, dest_port):
        proxy_type, proxy_addr, proxy_port, rdns, username, password = self.proxy
        self.connect((proxy_addr, proxy_port))
        self.sendall(b'\x04\x01' + struct.pack('!H', dest_port) + struct.pack('!4s', dest_addr))
        if self.recv(8) != b'\x00\x5a':
            raise socket.error('SOCKS4 proxy error')

    def _negotiate_SOCKS5(self, dest_addr, dest_port):
        proxy_type, proxy_addr, proxy_port, rdns, username, password = self.proxy
        self.connect((proxy_addr, proxy_port))
        self.sendall(b'\x05\x01\x00\x01' + struct.pack('!H', len(dest_addr)) + dest_addr + struct.pack('!H', dest_port))
        if self.recv(2) != b'\x05\x00':
            raise socket.error('SOCKS5 proxy error')
        if username and password:
            self.sendall(b'\x02' + struct.pack('!H', len(username)) + username + struct.pack('!H', len(password)) + password)
            if self.recv(2) != b'\x01':
                raise socket.error('SOCKS5 proxy authentication error')
        self.proxy_peername = self.getpeername()

    def _negotiate_HTTP(self, dest_addr, dest_port):
        proxy_type, proxy_addr, proxy_port, rdns, username, password = self.proxy
        self.connect((proxy_addr, proxy_port))
        request = f'CONNECT {dest_addr}:{dest_port} HTTP/1.1\r\nHost: {dest_addr}:{dest_port}\r\n\r\n'
        self.sendall(request.encode())
        if self.recv(12) != b'HTTP/1.1 200 OK\r\n':
            raise socket.error('HTTP proxy error')
