        'in_queue',
        'in_queue_bytes',
        'out_queue',
        'out_queue_bytes'
    ]

    IGNORED = [
        'service',
        'source',
        'timestamp',
        'version'
    ]

    def get_raw_stats(self, host, port):
        data = ''
        try:
            if port is None:
                sock = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)
                sock.connect(host)
            else:
                sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
                sock.connect((host, int(port)))

            stats_data = ''
            while True:
                data = sock.recv(1024)
                if not data:
                    break
                stats_data += data
            sock.close()

        except socket.error:
            raise Exception('Failed to get stats from %s:%s' % (host, port))

        try:
            return json.loads(stats_data)
        except (TypeError, ValueError):
            raise ValueError("Unable to parse response from Twemproxy as a json object")

    def get_stats(self, host, port):
        data = self.get_raw_stats(host, port)
        if data is None:
            return {}, {}

        stats = {}
        pools = {}
        for stat, value in data.iteritems():
            if isinstance(value, dict):
                pool_name = stat.replace('.', '_')
                pools[pool_name] = {}
                for pool_stat, pool_value in value.iteritems():
                    if isinstance(pool_value, dict):
                        server_name = pool_stat.replace('.', '_')
                        pools[pool_name][server_name] = {}
                        for server_stat, server_value in pool_value.iteritems():
                            pools[pool_name][server_name][server_stat] = int(server_value)
                    else:
                        pools[pool_name][pool_stat] = int(pool_value)
            else:
                if stat not in self.IGNORED:
                    stats[stat] = int(value)

        return stats, pools

    def collect_stats(self, hosts):
        if isinstance(hosts, basestring):
            hosts = [hosts]

        results = {}
        for host in hosts:
            matches = re.search('((.+)\@)?([^:]+)(:(\d+))?', host)
            alias = matches.group(2) or matches.group(3)
            hostname = matches.group(3)
            port = matches.group(5)

            stats, pools = self.get_stats(hostname, port)
            results[alias] = {
                'stats': stats,
                'pools': pools
            }

        return results

