        sock = socket.socket(socket.AF_INET if isinstance(addr, tuple) and len(addr) == 2 else socket.AF_UNIX)
        sock.connect(addr)
        return sock

    def _cleanup(self) -> None:
        self.close()
        SafeStreamSocket._id_numbers.remove(self._id)

    def _release_uid(self, id: int) -> None:
        if id in SafeStreamSocket._id_numbers:
            SafeStreamSocket._id_numbers.remove(id)

    def close(self) -> None:
        if self._sock:
            self._sock.close()
            self._sock = None

    @property
    def closed(self) -> bool:
        return self._sock is None

    @property
    def id(self) -> int:
        return self._id

    @property
    def name(self) -> str:
        return self._name

    @name.setter
    def name(self, new_name: str) -> None:
        self._name = new_name

    def get_socket(self) -> socket.socket:
        return self._sock

    def recv(self, bufsize: int = 4096) -> bytes:
        return self._sock.recv(bufsize)

    def recvall(self, size: int) -> bytearray:
        data = bytearray()
        while len(data) < size:
            packet = self._sock.recv(size - len(data))
            if not packet:
                break
            data.extend(packet)
        return data

    def sendall(self, data: Union[bytes, bytearray]) -> int:
        return self._sock.sendall(data)

    def switch_to_ssl(self, pemfile: str) -> None:
        self._dup_sock = ssl.wrap_socket(self._sock, certfile=pemfile, server_side=True)

    def __enter__(self) -> 'SafeStreamSocket':
        return self

    def __exit__(self, *args) -> None:
        self.close()
