class UDPSocketHandler:
    def recv_udp(self, listener: socket.socket, bufsize: int) -> tuple:
        msg, addr = listener.recvmsg(bufsize)
        srcip, dstip = addr[:2]
        return (srcip, dstip, msg)

    def send_udp(self, sock: socket.socket, srcip: tuple, dstip: tuple, data: bytes) -> None:
        sock.sendto(data, dstip)
        sock.setsockopt(socket.IPPROTO_IP, socket.IP_TOS, 0x10)
        sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_TTL, 1)
        sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_IF, srcip[0])

