import struct
import socket
from typing import Optional, Tuple, Any

IP_TRANSPARENT = 19
IP_ORIGDSTADDR = 20
IP_RECVORIGDSTADDR = IP_ORIGDSTADDR
SOL_IPV6 = 41
IPV6_ORIGDSTADDR = 74
IPV6_RECVORIGDSTADDR = IPV6_ORIGDSTADDR

class UDPSocketHandler:
    def __init__(self):
        self.recvmsg = self._detect_recvmsg_implementation()

    def _detect_recvmsg_implementation(self) -> Optional[str]:
        try:
            import socket as pythonsocket
            getattr(pythonsocket.socket, "recvmsg")
            return "python"
        except AttributeError:
            try:
                import socket_ext
                getattr(socket_ext.socket, "recvmsg")
                return "socket_ext"
            except ImportError:
                return None

    def recv_udp(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        if self.recvmsg == "python":
            return self._recv_udp_python(listener, bufsize)
        elif self.recvmsg == "socket_ext":
            return self._recv_udp_socket_ext(listener, bufsize)
        else:
            return self._recv_udp_fallback(listener, bufsize)

    def _recv_udp_python(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        data, ancdata, msg_flags, srcip = listener.recvmsg(4096, socket.CMSG_SPACE(24))
        dstip = None
        for cmsg_level, cmsg_type, cmsg_data in ancdata:
            if cmsg_level == socket.SOL_IP and cmsg_type == IP_ORIGDSTADDR:
                family, port = struct.unpack('=HH', cmsg_data[0:4])
                port = socket.htons(port)
                if family == socket.AF_INET:
                    start = 4
                    length = 4
                    ip = socket.inet_ntop(family, cmsg_data[start:start + length])
                    dstip = (ip, port)
                    break
            elif cmsg_level == SOL_IPV6 and cmsg_type == IPV6_ORIGDSTADDR:
                family, port = struct.unpack('=HH', cmsg_data[0:4])
                port = socket.htons(port)
                if family == socket.AF_INET6:
                    start = 8
                    length = 16
                    ip = socket.inet_ntop(family, cmsg_data[start:start + length])
                    dstip = (ip, port)
                    break
        return (srcip, dstip, data)

    def _recv_udp_socket_ext(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        srcip, data, adata, flags = listener.recvmsg((bufsize,), socket.CMSG_SPACE(24))
        dstip = None
        for a in adata:
            if a.cmsg_level == socket.SOL_IP and a.cmsg_type == IP_ORIGDSTADDR:
                family, port = struct.unpack('=HH', a.cmsg_data[0:4])
                port = socket.htons(port)
                if family == socket.AF_INET:
                    start = 4
                    length = 4
                    ip = socket.inet_ntop(family, a.cmsg_data[start:start + length])
                    dstip = (ip, port)
                    break
            elif a.cmsg_level == SOL_IPV6 and a.cmsg_type == IPV6_ORIGDSTADDR:
                family, port = struct.unpack('=HH', a.cmsg_data[0:4])
                port = socket.htons(port)
                if family == socket.AF_INET6:
                    start = 8
                    length = 16
                    ip = socket.inet_ntop(family, a.cmsg_data[start:start + length])
                    dstip = (ip, port)
                    break
        return (srcip, dstip, data[0])

    def _recv_udp_fallback(self, listener: socket.socket, bufsize: int) -> Tuple[Any, None, bytes]:
        data, srcip = listener.recvfrom(bufsize)
        return (srcip, None, data)

    def send_udp(self, sock: socket.socket, srcip: Optional[Tuple[str, int]], dstip: Tuple[str, int], data: bytes) -> None:
        if not srcip:
            return
        sender = socket.socket(sock.family, socket.SOCK_DGRAM)
        sender.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        sender.setsockopt(socket.SOL_IP, IP_TRANSPARENT, 1)
        sender.bind(srcip)
        sender.sendto(data, dstip)
        sender.close()

    def setup_udp_listener(self, udp_listener: socket.socket) -> None:
        udp_listener.setsockopt(socket.SOL_IP, IP_TRANSPARENT, 1)
        if hasattr(udp_listener, 'v4') and udp_listener.v4 is not None:
            udp_listener.v4.setsockopt(socket.SOL_IP, IP_RECVORIGDSTADDR, 1)
        if hasattr(udp_listener, 'v6') and udp_listener.v6 is not None:
            udp_listener.v6.setsockopt(SOL_IPV6, IPV6_RECVORIGDSTADDR, 1)

