        if not isBinary:
            msg = "{} - {}".format(self.prefix, payload.decode('utf8'))
            print(msg)
            self.sendMessage(msg.encode('utf8'))

class WebSocketEchoServer:
    def __init__(self, port=9000, paths=None):
        self.port = port
        self.paths = paths or {"echo1": "Echo 1", "echo2": "Echo 2"}
        self.resources = {}

    def create_factory(self, protocol_class, prefix):
        factory = WebSocketServerFactory()
        factory.protocol = protocol_class
        factory.prefix = prefix
        factory.startFactory()
        return WebSocketResource(factory)

    def run(self):
        for path, prefix in self.paths.items():
            protocol = lambda: EchoServerProtocol(prefix)
            self.resources[path] = self.create_factory(protocol, prefix)

        root = Data("", "text/plain")
        for path, resource in self.resources.items():
            root.putChild(path.encode('utf8'), resource)

        site = Site(root)
        reactor.listenTCP(self.port, site)
        reactor.run()

