    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        signature = hmac.new(signature_secret.encode(), payload, hashlib.sha256).hexdigest()
        return signature

    def _validate_response(self, response: httpx.Response) -> ExternalActionResponse:
        if response.status_code == 200:
            return ExternalActionResponse(response.status_code, response.json())
        else:
            return ExternalActionResponse(response.status_code, {}, f"Error: {response.status_code}")
