from httpx import Response

class AsyncHttpx:
    proxy: ClassVar[dict[str, Union[str, None]]] = {}

    async def get(self, url: Union[str, list[str]], *, params: Optional[dict[str, Any]] = None, headers: Optional[dict[str, str]] = None, cookies: Optional[dict[str, str]] = None, verify: bool = True, use_proxy: bool = True, proxy: Optional[dict[str, str]] = None, timeout: int = 30, **kwargs) -> Response:
        if isinstance(url, list):
            return await self._get_first_successful(urls, **kwargs)
        return await self._get_single(url, **kwargs)

    async def _get_first_successful(self, urls: list[str], **kwargs) -> Response:
        for url in urls:
            response = await self._get_single(url, **kwargs)
            if response.status_code == 200:
                return response
        return None

    async def _get_single(self, url: str, *, params: Optional[dict[str, Any]] = None, headers: Optional[dict[str, str]] = None, cookies: Optional[dict[str, str]] = None, verify: bool = True, use_proxy: bool = True, proxy: Optional[dict[str, str]] = None, timeout: int = 30, **kwargs) -> Response:
        proxy_config = self._get_proxy_config(use_proxy, proxy)
        return await httpx.get(url, params=params, headers=headers, cookies=cookies, verify=verify, proxy=proxy_config, timeout=timeout, **kwargs)

    async def post(self, url: str, *, data: Optional[dict[str, Any]] = None, content: Any = None, files: Any = None, verify: bool = True, use_proxy: bool = True, proxy: Optional[dict[str, str]] = None, json: Optional[dict[str, Any]] = None, params: Optional[dict[str, str]] = None, headers: Optional[dict[str, str]] = None, cookies: Optional[dict[str, str]] = None, timeout: int = 30, **kwargs) -> Response:
        proxy_config = self._get_proxy_config(use_proxy, proxy)
        return await httpx.post(url, data=data, content=content, files=files, json=json, params=params, headers=headers, cookies=cookies, verify=verify, proxy=proxy_config, timeout=timeout, **kwargs)

    async def download_file(self, url: Union[str, list[str]], path: Union[str, Path], *, params: Optional[dict[str, str]] = None, verify: bool = True, use_proxy: bool = True, proxy: Optional[dict[str, str]] = None, headers: Optional[dict[str, str]] = None, cookies: Optional[dict[str, str]] = None, timeout: int = 30, **kwargs) -> bool:
        response = await self.get(url, params=params, verify=verify, use_proxy=use_proxy, proxy=proxy, headers=headers, cookies=cookies, timeout=timeout)
        if response.status_code == 200:
            with open(path, 'wb') as f:
                f.write(response.content)
            return True
        return False

    def _get_proxy_config(self, use_proxy: bool, proxy: Optional[dict[str, str]]) -> dict[str, str]:
        if not use_proxy:
            return {}
        if proxy:
            return proxy
        return AsyncHttpx.proxy
