import requests
import urllib.parse
import json
from datetime import timedelta
import re

class RunKeeperAPI:
    _RATE_LIMIT_KEY = "rk:rate_limit:hit"

    def _rate_limit(self, endpoint, req_lambda):
        if redis.get(self._RATE_LIMIT_KEY) is not None:
            raise APIException("RK global rate limit previously reached on %s" % endpoint, user_exception=UserException(UserExceptionType.RateLimited))
        response = req_lambda()
        if response.status_code == 429:
            if "user" not in response.text:
                timeout = timedelta(minutes=15)
                timeout_match = re.search(r"(\d+) (second|minute|hour|day)", response.text)
                if timeout_match:
                    timeout = timedelta(**{"%ss" % timeout_match.group(2): float(timeout_match.group(1))}
                redis.setex(self._RATE_LIMIT_KEY, response.text, timeout)
                raise APIException("RK global rate limit reached on %s" % endpoint, user_exception=UserException(UserExceptionType.RateLimited))
            else:
                raise APIException("RK user rate limit reached on %s" % endpoint, user_exception=UserException(UserExceptionType.RateLimited))
        return response

    def _api_headers(self, service_record):
        return {"Authorization": "Bearer " + service_record.Authorization["Token"],
                "Accept-Charset": "UTF-8"}

    def get_request(self, url, service_record):
        response = self._rate_limit("get_request",
                                  lambda: requests.get(url,
                                                      headers=self._api_headers(service_record)))
        if response.status_code != 200:
            if response.status_code in [401, 403]:
                raise APIException("No authorization", block=True, user_exception=UserException(UserExceptionType.Authorization, intervention_required=True))
            raise APIException("Request failed: %s %s" % (response.status_code, response.text))
        return response.json()

    def post_request(self, url, service_record, data=None):
        response = self._rate_limit("post_request",
                                  lambda: requests.post(url,
                                                       headers=self._api_headers(service_record),
                                                       data=data))
        response.raise_for_status()
        return response

    def delete_request(self, url, service_record):
        response = self._rate_limit("delete_request",
                                  lambda: requests.delete(url,
                                                         headers=self._api_headers(service_record)))
        response.raise_for_status()
        return response

    def oauth_token_request(self, params):
        response = self._rate_limit("auth_token",
                                  lambda: requests.post("https://runkeeper.com/apps/token",
                                                      data=urllib.parse.urlencode(params),
                                                      headers={"Content-Type": "application/x-www-form-urlencoded"}))
        if response.status_code != 200:
            raise APIException("Invalid code")
        return response.json()["access_token"]

