import requests
import json

class AmazonSSO:
    def __init__(self, access_token: str = None, refresh_token: str = None, client_id: str = None, client_secret: str = None, user_pool_id: str = None, region: str = None):
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.client_id = client_id
        self.client_secret = client_secret
        self.user_pool_id = user_pool_id
        self.region = region
        self.user_info = {}

    def get_new_token(self) -> str:
        url = f"https://{self.region}.auth.amazonaws.com/cognito-idp/{self.user_pool_id}/token"
        payload = {
            'grant_type': 'refresh_token',
            'client_id': self.client_id,
            'client_secret': self.client_secret,
            'refresh_token': self.refresh_token
        }
        headers = {'Content-Type': 'application/x-www-form-urlencoded'}
        response = requests.post(url, data=payload, headers=headers)
        response.raise_for_status()
        return response.json()['access_token']

    def get_user_info(self) -> dict:
        url = f"https://{self.region}.auth.amazonaws.com/cognito-idp/{self.user_pool_id}/user/pool/{self.user_pool_id}/username"
        headers = {
            'Authorization': f'Bearer {self.access_token}',
            'Content-Type': 'application/json'
        }
        response = requests.get(url, headers=headers)
        response.raise_for_status()
        self.user_info = response.json()
        return self.user_info

    @staticmethod
    def amazon_sso(code: str, redirect_uri: str = None) -> tuple[AmazonSSO, None] | tuple[None, None]:
        url = f"https://{redirect_uri}/callback"
        payload = {
            'code': code,
            'client_id': 'YOUR_CLIENT_ID',
            'client_secret': 'YOUR_CLIENT_SECRET',
            'grant_type': 'authorization_code'
        }
        headers = {'Content-Type': 'application/x-www-form-urlencoded'}
        response = requests.post(url, data=payload, headers=headers)
        response.raise_for_status()
        tokens = response.json()
        return AmazonSSO(**tokens), None
