import requests
import os

def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    if not os.path.basename(wav_path).replace('.wav', '') == os.path.basename(txt_path).replace('.txt', ''):
        return None

    try:
        with open(wav_path, 'rb') as wav_file:
            wav_data = wav_file.read()
        with open(txt_path, 'r') as txt_file:
            txt_data = txt_file.read()

        url = f'http://{server_addr}:{port}/process'
        files = {
            'audio': ('audio.wav', wav_data, 'audio/wav'),
            'transcript': ('transcript.txt', txt_data, 'text/plain')
        }
        response = requests.post(url, files=files)

        if debug:
            print(response.json())

        return response
    except Exception as e:
        if debug:
            print(f'Error: {e}')
        return None
