        data = {
            'client_id': self.PULSSTORY_CLIENT_ID,
            'client_secret': self.PULSSTORY_CLIENT_SECRET,
            'code': code,
            'grant_type': 'authorization_code'
        }
        response = self._api_request('oauth2/token', data=data)
        return response['access_token']

    def revoke_authorization(self, service_record: object) -> None:
        data = self._apiData(service_record)
        self._api_request('oauth2/revoke', data=data)

    def download_activity(self, service_record: object, activity_id: str) -> dict:
        endpoint = f"activities/{activity_id}"
        response = self._api_request(endpoint, service_record)
        return response['activity']

    def upload_activity(self, service_record: object, upload_data: dict, uris: dict) -> str:
        compressed_data = zlib.compress(json.dumps(upload_data).encode('utf-8'))
        files = {'file': ('activity.json.zlib', compressed_data)}
        response = self._api_request(uris['upload'], service_record, files=files)
        return response['activity_id']
