    if param:
        url += '?' + urlencode(param)

    headers = {
        'Content-Type': 'application/json',
        'X-Auth-Token': Config.TOKEN,
        'X-Sdk-Date': time.strftime('%Y%m%dT%H%M%SZ', time.gmtime())
    }

    if isinstance(body, dict):
        body = json.dumps(body)

    string_to_sign = f"{method}\n{path}\n{headers['X-Sdk-Date']}\n{body or ''}"
    signature = hmac.new(Config.TOKEN.encode(), string_to_sign.encode(), hashlib.sha256).hexdigest()
    headers['Authorization'] = f"SDK-HMAC-SHA256 Access={Config.ID}, SignedHeaders=X-Sdk-Date, Signature={signature}"

    proxies = Config.PROXY if hasattr(Config, 'PROXY') else None

    response = requests.request(method, url, headers=headers, data=body, proxies=proxies)

    if response.status_code >= 300:
        raise Exception(f"HTTP Error: {response.status_code} - {response.text}")

    return response.json()
