from urllib.parse import urlparse
import socket
import time

class Timeout:
    DEFAULT_TIMEOUT = 60

class Retry:
    DEFAULT_RETRIES = 3

class HTTPConnectionPool:
    def __init__(self, host, port=None, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.host = host
        self.port = port or (443 if self.scheme == 'https' else 80)
        self.timeout = timeout
        self.retries = retries or Retry.DEFAULT_RETRIES
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.pool = []
        self.maxsize = maxsize
        self.block = block
        self.headers = headers or {}

    def _new_conn(self):
        if self.proxy:
            proxy_url = urlparse(self.proxy)
            conn = http.client.HTTPConnection(proxy_url.hostname, proxy_url.port, **self.conn_kw)
            conn.set_tunnel(self.host, self.port, self.proxy_headers)
        else:
            conn = http.client.HTTPConnection(self.host, self.port, **self.conn_kw)
        return conn

    def _make_request(self, conn, method, url, timeout=_Default, chunked=False, **httplib_request_kw):
        conn.request(method, url, **httplib_request_kw)
        response = conn.getresponse()
        return response

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        retries = retries or self.retries
        timeout = timeout or self.timeout
        headers = headers or self.headers
        conn = None

        while retries > 0:
            try:
                if self.pool:
                    conn = self.pool.pop(0)
                else:
                    conn = self._new_conn()

                response = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, body=body, headers=headers)

                if release_conn is None or release_conn:
                    self.pool.append(conn)

                return response

            except (socket.timeout, http.client.HTTPException) as e:
                retries -= 1
                if retries <= 0:
                    raise e
                time.sleep(1)

        if conn and (release_conn is None or release_conn):
            conn.close()
        raise Exception("Max retries exceeded")
