class RecordAccumulator:
    def __init__(self):
        self.batches = {}

    def add_record(self, topic_partition, record):
        if topic_partition not in self.batches:
            self.batches[topic_partition] = []
        self.batches[topic_partition].append(record)

    def drain(self):
        for topic_partition in list(self.batches.keys()):
            batch = self.batches.pop(topic_partition)
            # Simulate sending batch
            print(f"Sending batch for {topic_partition}: {batch}")

class ClusterMetadata:
    def __init__(self):
        self.metadata = {}

    def update(self, topic, partitions):
        self.metadata[topic] = partitions

class Sender(threading.Thread):
    def __init__(self, accumulator, producer):
        super().__init__()
        self.accumulator = accumulator
        self.producer = producer
        self.running = True

    def run(self):
        while self.running:
            self.accumulator.drain()
            time.sleep(1)

    def stop(self):
        self.running = False

class KafkaProducer:
    def __init__(self, **configs):
        self.config = configs
        self._producer = Producer(configs)
        self._metrics = Metrics()
        self._accumulator = RecordAccumulator()
        self._metadata = ClusterMetadata()
        self._sender = Sender(self._accumulator, self._producer)
        self._closed = False
        self._cleanup = self._cleanup_factory()
        atexit.register(self._cleanup)
        self._sender.start()

    def send(self, topic, value=None, key=None, headers=None, partition=None, timestamp_ms=None):
        def delivery_report(err, msg):
            if err is not None:
                print(f"Message delivery failed: {err}")
            else:
                print(f"Message delivered to {msg.topic()} [{msg.partition()}]")

        self._producer.produce(topic, value, key, headers, partition, timestamp_ms, delivery_report)
        self._producer.poll(0)

    def flush(self, timeout=None):
        self._producer.flush(timeout)

    def close(self, timeout=None):
        self._sender.stop()
        self._sender.join()
        self._producer.flush(timeout)
        self._closed = True
        self._unregister_cleanup()

    def _cleanup_factory(self):
        def cleanup():
            if not self._closed:
                self.close()
        return cleanup

    def _unregister_cleanup(self):
        atexit.unregister(self._cleanup)

    def _max_usable_produce_magic(self):
        return self._producer._max_usable_produce_magic()

    def _estimate_size_in_bytes(self, key, value, headers=[]):
        return len(key.encode('utf-8')) + len(value.encode('utf-8')) + sum(len(h[0].encode('utf-8')) + len(h[1].encode('utf-8')) for h in headers)

    def _ensure_valid_record_size(self, size):
        max_size = self.config.get('message_max_bytes', 1000000)
        if size > max_size:
            raise ValueError(f"Message size {size} exceeds maximum allowed {max_size}")

    def _wait_on_metadata(self, topic, max_wait):
        start_time = time.time()
        while topic not in self._metadata.metadata and (time.time() - start_time) < max_wait:
            self._producer.poll(0.1)
        return self._metadata.metadata.get(topic, set())

    def _serialize(self, f, topic, data):
        return f(data)

    def _partition(self, topic, partition, key, value, serialized_key, serialized_value):
