                memory_stats['total_memory'] += int(parts[0])

        return memory_stats

    def get_passenger_cpu_usage(self, dict_stats):
        total_cpu_usage = 0.0
        for proc in dict_stats.get('processes', []):
            command = f"{self.config.get('sudo', '')} ps -p {proc['pid']} -o %cpu"
            result = subprocess.run(command, shell=True, text=True, capture_output=True)
            cpu_usage = result.stdout.strip()
            if cpu_usage:
                total_cpu_usage += float(cpu_usage)
        return total_cpu_usage

    def get_passenger_queue_stats(self):
        command = f"{self.config.get('sudo', '')} passenger-status"
        result = subprocess.run(command, shell=True, text=True, capture_output=True)
        status_output = result.stdout
        queue_stats = {'top_level_queue': 0, 'app_queue': 0}

        top_level_match = re.search(r"Top-level queue size: (\d+)", status_output)
        if top_level_match:
            queue_stats['top_level_queue'] = int(top_level_match.group(1))

        app_queue_match = re.search(r"Application queue size: (\d+)", status_output)
        if app_queue_match:
            queue_stats['app_queue'] = int(app_queue_match.group(1))

        return queue_stats
