import socket
import select
import hmac
import hashlib
import os
import json
from Crypto.Cipher import AES
from Crypto.Util.Padding import pad, unpad

bind_host = 'localhost'
bind_port = 6666
real_key = b'secret_key'

SESS_KEY_NEG_START = 'SESS_KEY_NEG_START'
SESS_KEY_NEG_FINISH = 'SESS_KEY_NEG_FINISH'
DP_QUERY_NEW = 'DP_QUERY_NEW'

def encrypt(data, key):
    cipher = AES.new(key, AES.MODE_CBC)
    ct_bytes = cipher.encrypt(pad(data.encode('utf-8'), AES.block_size))
    iv = cipher.iv
    return iv + ct_bytes

def decrypt(data, key):
    iv = data[:16]
    ct = data[16:]
    cipher = AES.new(key, AES.MODE_CBC, iv)
    pt = unpad(cipher.decrypt(ct), AES.block_size)
    return pt.decode('utf-8')

def hmac_verify(data, key):
    hmac_obj = hmac.new(key, data, hashlib.sha256)
    return hmac_obj.digest()

def handle_client(client_socket, addr, sessions):
    try:
        data = client_socket.recv(1024)
        if not data:
            return False

        message = json.loads(decrypt(data, real_key))
        msg_type = message['type']
        session_id = message['session_id']

        if msg_type == SESS_KEY_NEG_START:
            temp_key = os.urandom(16)
            response = {
                'type': SESS_KEY_NEG_START,
                'temp_key': temp_key.hex()
            }
            encrypted_response = encrypt(json.dumps(response), real_key)
            client_socket.send(encrypted_response)
            sessions[session_id] = {'temp_key': temp_key, 'seq_num': 0}

        elif msg_type == SESS_KEY_NEG_FINISH:
            temp_key = sessions[session_id]['temp_key']
            hmac_check = hmac_verify(data, temp_key)
            if hmac_check == message['hmac']:
                sessions[session_id]['session_key'] = temp_key
                response = {'type': SESS_KEY_NEG_FINISH, 'status': 'success'}
                encrypted_response = encrypt(json.dumps(response), temp_key)
                client_socket.send(encrypted_response)

        elif msg_type == DP_QUERY_NEW:
            session_key = sessions[session_id]['session_key']
            response = {
                'type': DP_QUERY_NEW,
                'data': {'state': 'on', 'brightness': 80}
            }
            encrypted_response = encrypt(json.dumps(response), session_key)
            client_socket.send(encrypted_response)

        return True
    except (json.JSONDecodeError, KeyError, ValueError):
        client_socket.send(encrypt(json.dumps({'error': 'Invalid message'}), real_key))
        return False

def main():
    server_socket = socket.socket(socket.AF_INET6, socket.SOCK_STREAM)
    server_socket.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
    server_socket.bind((bind_host, bind_port))
    server_socket.listen()

    inputs = [server_socket]
    outputs = []
    sessions = {}

    while inputs:
        readable, writable, exceptional = select.select(inputs, outputs, inputs)
        for s in readable:
            if s is server_socket:
                client_socket, addr = s.accept()
                print(f'Connected by {addr}')
                inputs.append(client_socket)
            else:
                if not handle_client(s, addr, sessions):
                    inputs.remove(s)
                    s.close()

        for s in exceptional:
            inputs.remove(s)
            if s in outputs:
                outputs.remove(s)
            s.close()

if __name__ == '__main__':
    main()
