    def __init__(self):
        self.recvmsg: Optional[str] = None
        self._detect_recvmsg_implementation()

    def _detect_recvmsg_implementation(self) -> Optional[str]:
        if hasattr(socket.socket, 'recvmsg'):
            self.recvmsg = 'python'
        elif 'socket_ext' in sys.modules:
            self.recvmsg = 'socket_ext'
        return self.recvmsg

    def recv_udp(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        if self.recvmsg == 'python':
            return self._recv_udp_python(listener, bufsize)
        elif self.recvmsg == 'socket_ext':
            return self._recv_udp_socket_ext(listener, bufsize)
        else:
            return self._recv_udp_fallback(listener, bufsize)

    def _recv_udp_python(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        data, ancdata, flags, addr = listener.recvmsg(bufsize, socket.CMSG_LEN(1024))
        for cmsg_level, cmsg_type, cmsg_data in ancdata:
            if cmsg_level == socket.SOL_IP and cmsg_type == socket.IP_PKTINFO:
                src_ip = socket.inet_ntoa(cmsg_data[4:8])
                src_port = addr[1]
                return addr, (src_ip, src_port), data
        return addr, None, data

    def _recv_udp_socket_ext(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        data, ancdata, flags, addr = listener.recvmsg(bufsize, socket.CMSG_LEN(1024))
        for cmsg_level, cmsg_type, cmsg_data in ancdata:
            if cmsg_level == socket.SOL_IPV6 and cmsg_type == socket.IPV6_PKTINFO:
                src_ip = socket.inet_ntop(socket.AF_INET6, cmsg_data[8:24])
                src_port = addr[1]
                return addr, (src_ip, src_port), data
        return addr, None, data

    def _recv_udp_fallback(self, listener: socket.socket, bufsize: int) -> Tuple[Any, None, bytes]:
        data, addr = listener.recvfrom(bufsize)
        return addr, None, data

    def send_udp(self, sock: socket.socket, srcip: Optional[Tuple[str, int]], dstip: Tuple[str, int], data: bytes) -> None:
        if srcip:
            sock.bind(srcip)
        sock.sendto(data, dstip)

    def setup_udp_listener(self, udp_listener: socket.socket) -> None:
        udp_listener.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        if hasattr(socket, 'IP_TRANSPARENT'):
            udp_listener.setsockopt(socket.SOL_IP, socket.IP_TRANSPARENT, 1)
        if hasattr(socket, 'IPV6_TRANSPARENT'):
            udp_listener.setsockopt(socket.SOL_IPV6, socket.IPV6_TRANSPARENT, 1)
