    response = make_request('POST', url, json=params, headers=headers, retry=retry, proxy=proxy)
    return {k.lower(): v for k, v in response.headers.items()} if response else {}

def checkin(url, headers, retry, proxy=False):
    response = make_request('POST', url, headers=headers, retry=retry, proxy=proxy)
    if response:
        print("Check-in successful")
    else:
        print("Check-in failed")

def logout(url, headers):
    response = make_request('GET', url, headers=headers)
    return 0 if response else -3

