    def query_range(self, query: str, start: str, end: str, limit: int = 100) -> Dict:
        params = {
            'query': query,
            'start': start,
            'end': end,
            'limit': limit
        }
        response = requests.get(self.query_range_url, params=params)
        response.raise_for_status()
        return response.json()

    def create_tail_ws(self, query: str, limit: int = 100) -> 'LokiTailWs':
        ws_url = f"{self.tail_url}?query={query}&limit={limit}"
        ws = websocket.create_connection(ws_url)
        return LokiTailWs(ws)

class LokiTailWs:
    def __init__(self, ws: websocket.WebSocket) -> None:
        self._ws = ws

    def messages(self) -> Generator:
        while True:
            message = self._ws.recv()
            yield message

    def close(self) -> None:
        self._ws.close()
