import requests
from pathlib import Path
import sys

def download_schema_file(envs: Dict[str, Any]) -> Union[Path, None]:
    schema_file = envs.get('SIGMA_SCHEMA_FILE', 'sigma-schema.json')
    schema_url = envs['SIGMA_SCHEMA_URL']
    workspace = Path(envs['GITHUB_WORKSPACE'])
    local_path = workspace / schema_file

    if local_path.exists():
        return local_path.resolve()

    try:
        response = requests.get(schema_url)
        response.raise_for_status()
        local_path.write_bytes(response.content)
        return local_path.resolve()
    except requests.RequestException as e:
        print(f"Failed to download schema file: {e}")
        sys.exit(-1)

