class HTTPConnectionPool:
    scheme = 'http'
    ConnectionCls = HTTPConnection
    ResponseCls = HTTPResponse

    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.host = host
        self.port = port or 80
        self.timeout = timeout
        self.retries = retries or Retry()
        self.pool = queue.LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        for _ in range(maxsize):
            self.pool.put(None)

    def _new_conn(self):
        conn = self.ConnectionCls(self.host, self.port, **self.conn_kw)
        conn.timeout = self.timeout.total
        return conn

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except queue.Empty:
            conn = self._new_conn()
        return conn or self._new_conn()

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=False)
        except queue.Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, **httplib_request_kw):
        conn.request(method, url, **httplib_request_kw)
        response = conn.getresponse()
        return self.ResponseCls(response)

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, release_conn=None, **response_kw):
        if retries is None:
