        conn = self._get_conn(pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers, **response_kw)
            return response
        except (socket.error, HTTPException) as e:
            if retries.is_forced_retry(method, url, response=None, error=e):
