            return await value
        return value

    async def sse_generator(self, event_gen: AsyncGenerator) -> AsyncGenerator:
        async for event in event_gen:
            yield self.create_sse_event(event)

    def create_sse_event(self, data: Any) -> str:
        return f"data: {data}\n\n"

