class HTTPConnectionPool:
    def __init__(self, host, port=None, scheme='http', ConnectionCls=None, timeout=None, retries=None, proxy=None, proxy_headers=None, **conn_kw):
        self.scheme = scheme
        self.ConnectionCls = ConnectionCls or HTTPConnection
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.proxy = proxy
        self.proxy_headers = proxy_headers or {}
        self.conn_kw = conn_kw
        self.connections = []

    def _new_conn(self):
        conn = self.ConnectionCls(self.host, self.port, **self.conn_kw)
        self.connections.append(conn)
        return conn

    def _make_request(self, conn, method, url, timeout=None, chunked=False, **httplib_request_kw):
        if timeout is None:
            timeout = self.timeout
        conn.request(method, url, timeout=timeout, chunked=chunked, **httplib_request_kw)
        response = conn.getresponse()
        return response

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=None, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        if retries is None:
            retries = self.retries
        if timeout is None:
            timeout = self.timeout

        conn = self._new_conn()
        try:
            response = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, **response_kw)
            return response
        except Exception as e:
            if retries and retries > 0:
                return self.urlopen(method, url, body, headers, retries - 1, redirect, assert_same_host, timeout, pool_timeout, release_conn, chunked, **response_kw)
            else:
                raise e
        finally:
            if release_conn:
                conn.close()
                self.connections.remove(conn)

