class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=None, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.host = host
        self.port = port or 80
        self.timeout = timeout
        self.retries = retries
        self.pool = LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.headers = headers or {}
        self.conn_kw = conn_kw

    def _new_conn(self):
        return HTTPConnection(self.host, self.port, timeout=self.timeout, **self.conn_kw)

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=None, **httplib_request_kw):
        conn.request(method, url, headers=self.headers, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=None, pool_timeout=None, release_conn=None, **response_kw):
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers, **response_kw)
            if release_conn:
                self._put_conn(conn)
            return response
        except Exception as e:
            if retries and retries > 0:
                return self.urlopen(method, url, body, headers, retries - 1, redirect, assert_same_host, timeout, pool_timeout, release_conn, **response_kw)
            else:
                conn.close()
                raise e

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get()
            conn.close()


class HTTPSConnectionPool(HTTPConnectionPool):
    def __init__(self, host, port=None, strict=False, timeout=None, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, key_file=None, cert_file=None, cert_reqs=None, ca_certs=None, ssl_version=None, assert_hostname=None, assert_fingerprint=None, **conn_kw):
        super().__init__(host, port or 443, strict, timeout, maxsize, block, headers, retries, _proxy, _proxy_headers, **conn_kw)
        self.scheme = 'https'
        self.key_file = key_file
        self.cert_file = cert_file
        self.cert_reqs = cert_reqs
        self.ca_certs = ca_certs
        self.ssl_version = ssl_version
        self.assert_hostname = assert_hostname
        self.assert_fingerprint = assert_fingerprint

    def _prepare_conn(self, conn):
        conn.set_key_and_cert(self.key_file, self.cert_file)
        conn.set_cert_reqs(self.cert_reqs)
        conn.set_ca_certs(self.ca_certs)
        conn.set_ssl_version(self.ssl_version)
        conn.set_assert_hostname(self.assert_hostname)
        conn.set_assert_fingerprint(self.assert_fingerprint)

    def _new_conn(self):
        conn = HTTPSConnection(self.host, self.port, timeout=self.timeout, **self.conn_kw)
        self._prepare_conn(conn)
