class StaticServer(BaseHTTPRequestHandler):
    def __init__(self, data, prof_filename, *args, **kwargs):
        self.data = data
        self.prof_filename = prof_filename
        super().__init__(*args, **kwargs)

    def do_GET(self):
        self.send_response(200)

        if self.path == "/":
            self.send_header("Content-type", "text/html")
            self.end_headers()
            self.wfile.write(self._render().encode())
        else:
            this_dir = Path(__file__).resolve().parent
            filepath = this_dir / "web" / self.path[1:]

            mimetype, _ = mimetypes.guess_type(str(filepath))
            assert mimetype is not None
            self.send_header("Content-type", mimetype)
            self.end_headers()

            with open(filepath, "rb") as fh:
                content = fh.read()
            self.wfile.write(content)

    def _render(self):
        this_dir = Path(__file__).resolve().parent
        with open(this_dir / "web" / "index.html", encoding="utf-8") as _file:
            template = string.Template(_file.read())

        return template.substitute(
            data=html.escape(json.dumps(self.data).replace("</", "<\\/")),
            version=html.escape(__version__),
            filename=html.escape(self.prof_filename.replace("</", "<\\/")),
        )

class ProfileHTTPServer:
    def __init__(self, prof_filename: str, start_browser: bool = False, port: Optional[int] = None):
        self.prof_filename = prof_filename
        self.start_browser = start_browser
        self.port = port
        self.data = self._read_profile()

    def _read_profile(self):
        try:
            return read_import_profile(self.prof_filename)
        except (TunaError, StopIteration):
            pass
        return read_runtime_profile(self.prof_filename)

    def _is_port_in_use(self, port: int) -> bool:
        with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
            return s.connect_ex(("localhost", port)) == 0

    def start(self):
        if self.port is None:
            self.port = 8000
            while self._is_port_in_use(self.port):
                self.port += 1

        handler = lambda *args: StaticServer(self.data, self.prof_filename, *args)
        httpd = HTTPServer(("", self.port), handler)

        if self.start_browser:
            address = f"http://localhost:{self.port}"
            threading.Thread(target=lambda: webbrowser.open_new_tab(address)).start()

        print(f"Starting httpd on port {self.port}")
        httpd.serve_forever()

