    def send(self, topic, value=None, key=None, headers=None, partition=None, timestamp_ms=None):
        if self._closed:
            raise RuntimeError("Producer is closed")
        serialized_key = self._serialize(self.config.get('key_serializer'), topic, key)
        serialized_value = self._serialize(self.config.get('value_serializer'), topic, value)
        partition = self._partition(topic, partition, key, value, serialized_key, serialized_value)
        size = self._estimate_size_in_bytes(serialized_key, serialized_value, headers)
        self._ensure_valid_record_size(size)
        future = self._accumulator.append(topic, partition, serialized_key, serialized_value, headers, timestamp_ms)
        self._sender.wakeup()
        return future

    def flush(self, timeout=None):
        self._sender.flush(timeout)

    def close(self, timeout=None):
        if not self._closed:
            self._sender.close(timeout)
            self._unregister_cleanup()
            self._closed = True

    def _cleanup_factory(self):
        def cleanup():
            self.close()
        return cleanup

    def _unregister_cleanup(self):
        atexit.unregister(self._cleanup)

    def _max_usable_produce_magic(self):
        return self.config.get('api_version', 0)

    def _estimate_size_in_bytes(self, key, value, headers=[]):
        key_size = len(key) if key else 0
        value_size = len(value) if value else 0
        headers_size = sum(len(header) for header in headers) if headers else 0
        return key_size + value_size + headers_size

    def _ensure_valid_record_size(self, size):
        max_size = self.config.get('max_request_size', 1048576)
        if size > max_size:
            raise ValueError("Record size exceeds maximum allowed size")

    def _wait_on_metadata(self, topic, max_wait):
        return self._metadata.wait_for_metadata(topic, max_wait)

    def _serialize(self, serializer, topic, data):
        if serializer:
            return serializer(topic, data)
        return data

    def _partition(self, topic, partition, key, value, serialized_key, serialized_value):
        if partition is not None:
            return partition
        partitioner = self.config.get('partitioner')
        if partitioner:
            return partitioner(topic, key, value, serialized_key, serialized_value)
        return self._metadata.partition_for(topic, key)

import http.client
import urllib.parse
from collections import deque
from threading import Lock
from time import sleep

class Timeout:
    DEFAULT_TIMEOUT = 5

class Retry:
    DEFAULT_RETRIES = 3

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.host = host
        self.port = port or 80
        self.timeout = timeout
        self.retries = retries or Retry.DEFAULT_RETRIES
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.pool = deque(maxlen=maxsize)
        self.lock = Lock()
        self.block = block

    def _new_conn(self):
        return http.client.HTTPConnection(self.host, self.port, timeout=self.timeout, **self.conn_kw)

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, chunked=False, **httplib_request_kw):
        conn.request(method, url, headers=httplib_request_kw.get('headers'), body=httplib_request_kw.get('body'))
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        retries = retries or self.retries
        headers = headers or {}
        response = None

        for attempt in range(retries):
            conn = None
            try:
                with self.lock:
                    if self.pool:
                        conn = self.pool.popleft()
                    else:
                        conn = self._new_conn()

                response = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, headers=headers, body=body)
                if response.status in (301, 302, 303, 307) and redirect:
                    url = response.getheader('Location')
                    continue
                break
            except Exception as e:
                if attempt == retries - 1:
                    raise
                sleep(1)
            finally:
                if conn:
                    with self.lock:
                        self.pool.append(conn)

        return response

