class OssecCollector:
    def __init__(self, bin_path='/var/ossec/bin/agent_control', use_sudo=True, sudo_cmd='/usr/bin/sudo'):
        self.bin_path = bin_path
        self.use_sudo = use_sudo
        self.sudo_cmd = sudo_cmd

    def collect(self):
        command = [self.bin_path, '-l']

        if str_to_bool(self.use_sudo):
            command.insert(0, self.sudo_cmd)

        try:
            p = subprocess.Popen(command, stdout=subprocess.PIPE)
            res = p.communicate()[0]
        except Exception as e:
            raise Exception('Unable to exec cmd: %s, because %s' % (' '.join(command), str(e)))

        if res == '':
            raise Exception('Empty result from exec cmd: %s' % (' '.join(command)))

        states = {}
        for line in res.split("\n"):
            if not line.startswith('   ID: '):
                continue
            fragments = line.split(',')
            state = fragments[-1].lstrip()
            if state not in states:
                states[state] = 1
            else:
                states[state] += 1

        return states

