        response = self.requests.post(url, headers=headers, data=data, proxies={"http": self.PROXY, "https": self.PROXY})
        response.raise_for_status()
        result = response.json()
        if result.get("status", {}).get("code") != "1":
            raise Exception(f"API Error: {result.get('status', {}).get('message')}")
        return result

    def get_domain_info(self, domain):
        parts = domain.split('.')
        if len(parts) < 2:
            raise ValueError("Invalid domain format")
        main_domain = '.'.join(parts[-2:])
        sub_domain = '.'.join(parts[:-2]) if len(parts) > 2 else '@'
        domain_id = self.get_domain_id(main_domain)
        return domain_id, sub_domain

    def get_domain_id(self, domain):
        if domain in self.domain_list:
            return self.domain_list[domain]
        result = self.request("Domain.List")
        for item in result.get("domains", []):
            if item.get("name") == domain:
                self.domain_list[domain] = item.get("id")
                return item.get("id")
        raise Exception(f"Domain {domain} not found")

    def get_records(self, did, **conditions):
        if did in self.records:
            return self.records[did]
        result = self.request("Record.List", {"domain_id": did})
        records = result.get("records", [])
        self.records[did] = {record["id"]: record for record in records}
        if conditions:
            filtered_records = {}
            for rid, record in self.records[did].items():
                if all(record.get(k) == v for k, v in conditions.items()):
                    filtered_records[rid] = record
            return filtered_records
        return self.records[did]

    def update_record(self, domain, value, record_type="A"):
        domain_id, sub_domain = self.get_domain_info(domain)
        records = self.get_records(domain_id, name=sub_domain, type=record_type)
        if records:
            record_id = next(iter(records))
            return self.request("Record.Modify", {
                "domain_id": domain_id,
                "record_id": record_id,
                "sub_domain": sub_domain,
                "record_type": record_type,
                "record_line": self.DEFAULT,
                "value": value,
                "ttl": self.TTL
            })
        else:
            return self.request("Record.Create", {
                "domain_id": domain_id,
                "sub_domain": sub_domain,
                "record_type": record_type,
                "record_line": self.DEFAULT,
                "value": value,
                "ttl": self.TTL
            })

