            raise TooManyRequestsException("Too many requests to the DeepL API.")
        elif response.status_code != 200:
            return None

        translations = response.json().get("translations", [])
        if numberAlternative <= 1:
            translated_text = translations[0].get("text", "")
            if printResult:
                print(translated_text)
            return translated_text
        else:
            alternative_texts = [t.get("text", "") for t in translations]
            if printResult:
                for alt in alternative_texts:
                    print(alt)
            return alternative_texts
    except httpx.RequestError as e:
        return None

