        """Check if API web is accessible
        :return: Tuple containing (title, url) if successful, None otherwise
        """
        if url is None:
            return None

        with httpx.Client(headers=self.header, verify=self.verify) as client:
            try:
                resp = client.get(url=url).text
                title = re.findall('<title>(.*?)</title>', resp)
                if title:
                    logger.info(f"{url} title:{title[0]}")
                    return (title[0], url)
            except httpx.HTTPError as why:
                logger.error(f"{url} request error! {why}")
        return None

    def get_request(self, url: str, params: dict = None) -> requests.Response:
        """Send GET request using requests library
        :return: Response object
        """
        try:
            return requests.get(
                url,
                headers=self.header,
                params=params,
                timeout=8,
                verify=self.verify
            )
        except Exception as e:
            logger.error(f"GET request failed: {e}")
            raise

    def client_get(self, url: str) -> httpx.Response:
        """Send GET request using httpx client
        :return: Response object
        """
        with httpx.Client(verify=self.verify) as client:
            return client.get(url, headers=self.header)

