import ssl

class Client:
    def __init__(self):
        self.seq = 0
        self.sock = None
        self.sync_key = 0
        self.syncing = False
        self.sync_timestamp = 0
        self.next_sync_key = 0

    def connect_server(self, device_id, token, host=None, port=24430, use_ssl=True):
        address = (host if host else "imnode2.gobelieve.io", port)
        if use_ssl:
            sock_fd = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            context = ssl.create_default_context()
            sock = context.wrap_socket(sock_fd, server_hostname=address[0])
        else:
            sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)

        sock.connect(address)
        auth = AuthenticationToken()
        auth.token = token
        auth.device_id = device_id
        self.seq = self.seq + 1
        send_message(MSG_AUTH_TOKEN, self.seq, auth, sock)
        cmd, _, _, msg = recv_message(sock)
        if cmd != MSG_AUTH_STATUS or msg != 0:
            return False

        self.sock = sock
        return True

    def close(self):
        if self.sock:
            self.sock.close()

    def recv_message(self):
        while True:
            rlist, _, xlist = select.select([self.sock], [], [self.sock], 60)
            if not rlist and not xlist:
                self.seq += 1
                send_message(MSG_PING, self.seq, None, self.sock)
                continue
            if xlist:
                return 0, 0, None
            if rlist:
                cmd, s, _, m = recv_message(self.sock)
                self.handle_message(cmd, s, m)
                return cmd, s, m

    def handle_message(self, cmd, s, m):
        if cmd == MSG_IM:
            self.ack_message(s)
        elif cmd == MSG_GROUP_IM:
            self.ack_message(s)
        elif cmd == MSG_SYSTEM:
            self.ack_message(s)
        elif cmd == MSG_SYNC_NOTIFY:
            new_sync_key = m
            now = int(time.time())
            if new_sync_key > self.sync_key and (not self.syncing or now - self.sync_timestamp > 3):
                self.send_sync()
                self.syncing = True
                self.sync_timestamp = int(time.time())
            elif new_sync_key > self.next_sync_key:
                self.next_sync_key = new_sync_key
        elif cmd == MSG_SYNC_END:
            new_sync_key = m
            if new_sync_key > self.sync_key:
                self.sync_key = new_sync_key
                self.send_sync_key()
            self.syncing = False
            if self.next_sync_key > self.sync_key:
                self.send_sync()
                self.syncing = True
                self.sync_timestamp = int(time.time())
                self.next_sync_key = 0

    def ack_message(self, s):
        self.seq += 1
        send_message(MSG_ACK, self.seq, s, self.sock)

    def send_peer_message(self, msg):
        self.seq += 1
        send_message(MSG_IM, self.seq, msg, self.sock)

    def send_group_message(self, msg):
        self.seq += 1
        send_message(MSG_GROUP_IM, self.seq, msg, self.sock)

    def send_sync(self):
        self.seq += 1
        send_message(MSG_SYNC, self.seq, self.sync_key, self.sock)

    def send_sync_key(self):
        self.seq += 1
        send_message(MSG_SYNC_KEY, self.seq, self.sync_key, self.sock)

