from typing import Dict, List, Set, Union

async def fetch_contributor_activity(owner: str, repo: str, since: datetime, max: int = 100, excluded_users: Set[str] = None, token: str = None) -> Dict[str, Dict[str, Union[str, List]]]:
    headers = {'Authorization': f'token {token}'} if token else {}
    url = f'https://api.github.com/repos/{owner}/{repo}/events'
    params = {'per_page': max}

    async with aiohttp.ClientSession() as session:
        async with session.get(url, headers=headers, params=params) as response:
            events = await response.json()

    activity = {}

    for event in events:
        if 'created_at' not in event or datetime.fromisoformat(event['created_at'][:-1]) < since:
            continue

        actor = event['actor']['login']
        if excluded_users and actor in excluded_users:
            continue

        if actor not in activity:
            activity[actor] = {
                "created_issues": [],
                "created_pull_requests": [],
                "merged_commits": []
            }

        if event['type'] == 'IssuesEvent' and event['payload']['action'] == 'opened':
            activity[actor]["created_issues"].append(event['payload']['issue']['html_url'])

        elif event['type'] == 'PullRequestEvent' and event['payload']['action'] == 'opened':
            activity[actor]["created_pull_requests"].append(event['payload']['pull_request']['html_url'])

        elif event['type'] == 'PullRequestEvent' and event['payload']['action'] == 'closed' and event['payload']['pull_request']['merged']:
            activity[actor]["merged_commits"].append(event['payload']['pull_request']['html_url'])

    return activity

# Example usage:
# asyncio.run(fetch_contributor_activity('owner', 'repo', datetime(2023, 1, 1), excluded_users={'bot_user'}, token='your_github_token'))

