from twisted.internet import reactor
from autobahn.twisted.websocket import WebSocketServerProtocol, WebSocketServerFactory
from autobahn.websocket.compress import PerMessageDeflateOffer, PerMessageDeflateOfferAccept

class EchoServerProtocol(WebSocketServerProtocol):
    def onConnect(self, request):
        print("WebSocket connection request by {}".format(request.peer))

    def onOpen(self):
        print("WebSocket extensions in use: {}".format(self.websocket_extensions_in_use))

    def onMessage(self, payload, isBinary):
        self.sendMessage(payload, isBinary)

class WebSocketServer:
    def __init__(self, host="127.0.0.1", port=9000):
        self.host = host
        self.port = port
        self.factory = WebSocketServerFactory(f"ws://{host}:{port}")
        self.factory.protocol = EchoServerProtocol

        def accept(offers):
            for offer in offers:
                if isinstance(offer, PerMessageDeflateOffer):
                    return PerMessageDeflateOfferAccept(offer)

        self.factory.setProtocolOptions(perMessageCompressionAccept=accept)

    def run(self):
        reactor.listenTCP(self.port, self.factory)
        reactor.run()

