import socket
from http.client import HTTPConnection, HTTPResponse
from queue import LifoQueue, Empty
from urllib.parse import urlparse

class Timeout:
    DEFAULT_TIMEOUT = socket._GLOBAL_DEFAULT_TIMEOUT

class Retry:
    DEFAULT_RETRIES = 3

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = "http"
        self.ConnectionCls = HTTPConnection
        self.ResponseCls = HTTPResponse
        self.host = host
        self.port = port or 80
        self.timeout = timeout
        self.retries = retries or Retry.DEFAULT_RETRIES
        self.pool = LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw

    def _new_conn(self):
        conn = self.ConnectionCls(self.host, self.port, timeout=self.timeout, **self.conn_kw)
        return conn

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, **httplib_request_kw):
        conn.request(method, url, **httplib_request_kw)
        response = conn.getresponse()
        return self.ResponseCls(response)

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, **response_kw):
        retries = retries or self.retries
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers, **response_kw)
            return response
        finally:
            self._put_conn(conn)

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get_nowait()
            conn.close()

