from PIL import Image
import base64
import requests
from io import BytesIO
from typing import Union

class OpenAIGPT4Vision:
    def __init__(self, api_key: str, max_new_tokens: int = 256) -> None:
        self.api_key = api_key
        self.max_new_tokens = max_new_tokens
        self.headers = {
            'Content-Type': 'application/json',
            'Authorization': f'Bearer {self.api_key}'
        }

    @staticmethod
    def encode_image_to_base64(raw_image_data: Image.Image) -> str:
        buffered = BytesIO()
        raw_image_data.save(buffered, format="PNG")
        return base64.b64encode(buffered.getvalue()).decode('utf-8')

    def generate(self, text_prompt: str, raw_image_data: Union[Image.Image, dict, str]) -> str:
        image = self._get_pil_image(raw_image_data)
        image_base64 = self.encode_image_to_base64(image)

        payload = {
            "prompt": text_prompt,
            "image": image_base64,
            "max_new_tokens": self.max_new_tokens
        }

        response = self._post_with_retry("https://api.openai.com/v1/gpt-4-vision", payload)
        return response.get('generated_text', '')

    @staticmethod
    def _get_pil_image(raw_image_data: Union[Image.Image, dict, str]) -> Image.Image:
        if isinstance(raw_image_data, Image.Image):
            return raw_image_data
        elif isinstance(raw_image_data, dict) and 'url' in raw_image_data:
            response = requests.get(raw_image_data['url'])
            return Image.open(BytesIO(response.content))
        elif isinstance(raw_image_data, str):
            return Image.open(BytesIO(base64.b64decode(raw_image_data)))
        else:
            raise ValueError("Unsupported image data format")

    def _post_with_retry(self, url: str, payload: dict, retries: int = 3) -> dict:
        for attempt in range(retries):
            try:
                response = requests.post(url, json=payload, headers=self.headers)
                if response.status_code == 200:
                    return response.json()
            except requests.RequestException as e:
                if attempt == retries - 1:
                    raise e
        return {}

import requests

def fetch_stock_data(symbol):
    url = f"https://query1.finance.yahoo.com/v7/finance/quote?symbols={symbol}"
    response = requests.get(url)

    if response.status_code != 200:
        raise ValueError(f"HTTP request failed with status code {response.status_code}")

    data = response.json()
    if 'quoteResponse' not in data or 'result' not in data['quoteResponse'] or not data['quoteResponse']['result']:
