        if response.status_code != 200:
            raise Exception(f"Failed to submit task: {response.text}")

        task_id = response.json().get('task_id')
        if not task_id:
            raise Exception("Task ID not returned from submission")

        start_time = time.time()
        while time.time() - start_time < timeout:
            result_response = requests.get(f"{self.base_url}/result/{task_id}", headers=self.headers)
            if result_response.status_code == 200:
                return result_response.json()
            elif result_response.status_code == 202:
                time.sleep(5)
            else:
                raise Exception(f"Error retrieving result: {result_response.text}")

        raise TimeoutError("Task did not complete within the specified timeout")

    def submit_sync(self, request_data: Dict[str, Any]) -> Dict[str, Any]:
        response = requests.post(f"{self.base_url}/submit_sync", json=request_data, headers=self.headers)
        if response.status_code != 200:
            raise Exception(f"Failed to submit synchronous task: {response.text}")
        return response.json()

