import sqlite3
from sklearn.feature_extraction.text import CountVectorizer
from sklearn.naive_bayes import MultinomialNB

class ReviewClassifier:
    def __init__(self):
        self.app = Flask(__name__)
        self.clf = pickle.load(open('classifier.pkl', 'rb'))
        self.vect = pickle.load(open('vectorizer.pkl', 'rb'))
        self.db = 'reviews.db'
        self.setup_routes()

    def setup_routes(self):
        self.app.add_url_rule('/', 'index', self.index)
        self.app.add_url_rule('/results', 'results', self.results, methods=['POST'])
        self.app.add_url_rule('/feedback', 'feedback', self.feedback, methods=['POST'])

    def classify(self, document):
        X = self.vect.transform([document])
        prob = self.clf.predict_proba(X)[0]
        sentiment_label = 'positive' if prob[1] > prob[0] else 'negative'
        return sentiment_label, max(prob)

    def train(self, document, y):
        X = self.vect.transform([document])
        self.clf.partial_fit(X, [y])

    def sqlite_entry(self, document, y):
        conn = sqlite3.connect(self.db)
        cursor = conn.cursor()
        cursor.execute("CREATE TABLE IF NOT EXISTS reviews (review TEXT, sentiment INTEGER)")
        cursor.execute("INSERT INTO reviews (review, sentiment) VALUES (?, ?)", (document, y))
        conn.commit()
        conn.close()

    def index(self):
        return render_template('index.html')

    def results(self):
        review = request.form['review']
        sentiment_label, probability = self.classify(review)
        return render_template('results.html', review=review, sentiment=sentiment_label, probability=probability)

    def feedback(self):
        review = request.form['review']
        sentiment = int(request.form['sentiment'])
        self.train(review, sentiment)
        self.sqlite_entry(review, sentiment)
        return redirect(url_for('index'))

    def run(self):
        self.app.run(debug=True)

if __name__ == '__main__':
    classifier = ReviewClassifier()
    classifier.run()

