from datetime import datetime

class AppConfig:
    def __init__(self, simulate_error=False, require_auth=False):
        self.simulate_error = simulate_error
        self.require_auth = require_auth

class FlaskAPI:
    def __init__(self, config: AppConfig):
        self.app = Flask(__name__)
        self.config = config
        self.next_user_id = 1
        self.last_modified = datetime.now().isoformat()
        self.users = {}
        self.deleted_orders = set()
        self._setup_routes()

    def _setup_routes(self):
        self.app.add_url_rule('/spec', 'get_spec', self.get_spec, methods=['GET'])
        self.app.add_url_rule('/users', 'list_users', self.list_users, methods=['GET'])
        self.app.add_url_rule('/users', 'create_user', self.create_user, methods=['POST'])
        self.app.add_url_rule('/users/<int:user_id>', 'get_user', self.get_user, methods=['GET'])
        self.app.add_url_rule('/users/<int:user_id>', 'update_user', self.update_user, methods=['PUT'])
        self.app.add_url_rule('/users/<int:user_id>', 'delete_user', self.delete_user, methods=['DELETE'])
        self.app.add_url_rule('/orders/<string:order_id>', 'delete_order', self.delete_order, methods=['DELETE'])
        self.app.before_request(self.check_auth)

    def get_spec(self):
        spec = {
            "openapi": "3.0.0",
            "info": {
                "title": "User Management API",
                "version": "1.0.0"
            },
            "paths": {
                "/users": {
                    "get": {"summary": "List all users"},
                    "post": {"summary": "Create a new user"}
                },
                "/users/{user_id}": {
                    "get": {"summary": "Get a user by ID"},
                    "put": {"summary": "Update a user by ID"},
                    "delete": {"summary": "Delete a user by ID"}
                },
                "/orders/{order_id}": {
                    "delete": {"summary": "Delete an order"}
                }
            }
        }
        return jsonify(spec)

    def get_user(self, user_id: int):
        if self.config.simulate_error:
            return Response("Internal Server Error", status=500)
        user = self.users.get(user_id)
        if user:
            return jsonify(user)
        return Response("User not found", status=404)

    def list_users(self):
        if self.config.simulate_error:
            return Response("Internal Server Error", status=500)
        return jsonify(list(self.users.values()))

    def create_user(self):
        if self.config.simulate_error:
            return Response("Internal Server Error", status=500)
        data = request.json
        user_id = self.next_user_id
        self.users[user_id] = {"id": user_id, "name": data.get("name"), "email": data.get("email")}
        self.next_user_id += 1
        self.last_modified = datetime.now().isoformat()
        return jsonify(self.users[user_id]), 201

    def update_user(self, user_id: int):
        if self.config.simulate_error:
            return Response("Internal Server Error", status=500)
        user = self.users.get(user_id)
        if not user:
            return Response("User not found", status=404)
        data = request.json
        user.update({"name": data.get("name"), "email": data.get("email")})
        self.last_modified = datetime.now().isoformat()
        return jsonify(user)

    def delete_user(self, user_id: int):
        if self.config.simulate_error:
            return Response("Internal Server Error", status=500)
        if user_id in self.users:
            del self.users[user_id]
            self.last_modified = datetime.now().isoformat()
            return Response("User deleted", status=204)
        return Response("User not found", status=404)

    def delete_order(self, order_id: str):
        if self.config.simulate_error:
            return Response("Internal Server Error", status=500)
        self.deleted_orders.add(order_id)
        return Response("Order deleted", status=204)

    def check_auth(self):
        if self.config.require_auth:
            auth = request.headers.get("Authorization")
            if not auth or auth != "Bearer valid_token":
                return Response("Unauthorized", status=401)

