class HTTPConnectionPool:
    def __init__(self, host, port=None, timeout=None, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http' if port == 80 else 'https'
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.pool = queue.Queue(maxsize)
        self.block = block
        self.headers = headers or {}

    def _new_conn(self):
        if self.scheme == 'https':
            return http.client.HTTPSConnection(self.host, self.port, timeout=self.timeout, **self.conn_kw)
        return http.client.HTTPConnection(self.host, self.port, timeout=self.timeout, **self.conn_kw)

    def _make_request(self, conn, method, url, timeout=None, chunked=False, **httplib_request_kw):
        try:
            conn.request(method, url, headers=self.headers, **httplib_request_kw)
            response = conn.getresponse()
            return response
        except Exception as e:
            conn.close()
            raise e

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=None, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        if retries is None:
            retries = self.retries
        if headers is None:
            headers = self.headers

        conn = None
        try:
            conn = self.pool.get(block=self.block, timeout=pool_timeout)
        except queue.Empty:
            conn = self._new_conn()

        try:
            response = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, body=body, headers=headers, **response_kw)
            if release_conn:
                self.pool.put(conn)
            return response
        except Exception as e:
            if retries and retries > 0:
                return self.urlopen(method, url, body, headers, retries - 1, redirect, assert_same_host, timeout, pool_timeout, release_conn, chunked, **response_kw)
            else:
                raise e
        finally:
            if not release_conn:
                conn.close()

