class ONNXModifierServer:
    def __init__(self):
        self.app = Flask(__name__)
        self.onnx_modifier = None
        self._setup_routes()

    def _setup_routes(self):
        self.app.add_url_rule('/', 'index', self.index, methods=['GET'])
        self.app.add_url_rule('/open_model', 'open_model', self.open_model, methods=['POST'])
        self.app.add_url_rule('/modify_and_download_model', 'modify_and_download_model', self.modify_and_download_model, methods=['POST'])

    def index(self):
        return render_template('index.html')

    def open_model(self):
        if 'file' not in request.files:
            return jsonify({'error': 'No file part'}), 400
        file = request.files['file']
        if file.filename == '':
            return jsonify({'error': 'No selected file'}), 400
        temp_dir = tempfile.mkdtemp()
        file_path = os.path.join(temp_dir, file.filename)
        file.save(file_path)
        self.onnx_modifier = onnxModifier(file_path)
        return jsonify({'message': 'Model loaded successfully'}), 200

    def modify_and_download_model(self):
        if not self.onnx_modifier:
            return jsonify({'error': 'No model loaded'}), 400
        modifications = request.get_json()
        if not modifications:
            return jsonify({'error': 'No modifications provided'}), 400
        self.onnx_modifier.apply_modifications(modifications)
        temp_dir = tempfile.mkdtemp()
        modified_model_path = os.path.join(temp_dir, 'modified_model.onnx')
        self.onnx_modifier.save(modified_model_path)
        return send_file(modified_model_path, as_attachment=True)

    def run(self, host='127.0.0.1', port=5000, debug=False):
        self.app.run(host=host, port=port, debug=debug)

if __name__ == '__main__':
    server = ONNXModifierServer()
    server.run()

