import os
import ssl

def create_flask_app(directory, port, password, ssl_enabled):
    app = Flask(__name__)
    auth = HTTPBasicAuth()

    @auth.verify_password
    def verify_password(username, password_input):
        return password_input == password

    @app.route('/<path:path>', methods=['GET'])
    @app.route('/', defaults={'path': ''}, methods=['GET'])
    @auth.login_required
    def home(path):
        full_path = os.path.join(directory, path)
        if os.path.isdir(full_path):
            files = os.listdir(full_path)
            file_links = [f'<a href="{url_for("home", path=os.path.join(path, f))}">{f}</a>' for f in files]
            return '<br>'.join(file_links)
        elif os.path.isfile(full_path):
            return send_from_directory(directory, path, as_attachment=True)
        else:
            abort(404)

    @app.route('/upload', methods=['POST'])
    @auth.login_required
    def upload():
        if 'file' not in request.files:
            return redirect(request.url)
        file = request.files['file']
        if file.filename == '':
            return redirect(request.url)
        filename = secure_filename(file.filename)
        upload_path = os.path.join(directory, filename)
        file.save(upload_path)
        return redirect(url_for('home', path=''))

    if ssl_enabled:
        context = ssl.SSLContext(ssl.PROTOCOL_TLS)
        context.load_cert_chain('cert.pem', 'key.pem')
        app.run(host='0.0.0.0', port=port, ssl_context=context)
    else:
        app.run(host='0.0.0.0', port=port)

