    sid = str(uuid.uuid4())
    qr_url = f"http://example.com/auth/{sid}"
    qr_status[sid] = 'pending'

    # Generate QR code image
    qr = qrcode.make(qr_url)
    qr_path = f"static/{sid}.png"
    qr.save(qr_path)

    return jsonify({'qr_url': qr_url, 'qr_image': qr_path})

