class Stream:
    def __init__(self, server, port=80, headers={}, url="/", ssl_enabled=False, ssl_verification_enabled=True):
        self.maxtries = 5
        self._tries = 0
        self._delay = 1
        self._closed = False
        self._server = server
        self._port = port
        self._headers = headers
        self._url = url
        self._ssl_enabled = ssl_enabled
        self._ssl_verification_enabled = ssl_verification_enabled
        self._connect()

    def write(self, data, reconnect_on=("", 200, 502)):
        if not self._isconnected():
            response = self._getresponse()
            if (response == "" and "" in reconnect_on) or (
                response and isinstance(response, http.client.HTTPResponse) and response.status in reconnect_on
            ):
                self._reconnect()
            elif response and isinstance(response, http.client.HTTPResponse):
                raise Exception(
                    "Server responded with status code: {status_code}\nand message: {msg}.".format(
                        status_code=response.status, msg=response.read()
                    )
                )
            elif response == "":
                raise Exception("Attempted to write but socket was not connected.")

        try:
            msg = data
            msglen = format(len(msg), "x")
            self._conn.sock.setblocking(1)
            self._conn.send(
                "{msglen}\r\n{msg}\r\n".format(msglen=msglen, msg=msg).encode("utf-8")
            )
            self._conn.sock.setblocking(0)
        except http.client.socket.error:
            self._reconnect()
            self.write(data)

    def _get_ssl_context(self):
