        self._bytes = b""
        self._reset_retries()
        time.sleep(0.5)

    def close(self):
        self._reset_retries()
        self._closed = True
        try:
            self._conn.send("\r\n0\r\n\r\n".encode("utf-8"))
        except http.client.socket.error:
